import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'

import axios from 'axios'
const Home = () => {
    const [items, setItems] = useState([])
    useEffect(() => {
        axios.get('http://localhost:3001/items/')
            .then(response => setItems(response.data))
            .catch(err => console.log(err))
    }, [])



    return (
        <div>
            <h2 className="welcome">Thank You For Shopping Us!</h2>
            <div className="product-container">
                {items.map(item => ((
                    <div key={item.name} className="product">
                        <h2 className="product-title">{item.name}</h2>
                        <img className="product-logo" src={item.product_img} alt={item.name} />
                        <p className="product-description"> {item.description}</p>
                        <h1 className="product-price">${item.price}</h1>
                        <Link to={`/${item.id}`}><button className="product-btn">Buy</button></Link>
                    </div>

                )))}
            </div>
        </div>
    )
}

export default Home
