import React, { useState } from 'react'

const Login = () => {
    const [userField, setUserField] = useState({
        email: '',
        password: '',
        confirm_password: '',
    })
    function handleForm(e) {
        const { name, value } = e.target
        setUserField(prevState => {
            return {
                ...prevState,
                [name]: value
            }
        })
    }
    console.log(userField);
    return (
        <div>
            <form className="login_form">
                <div className="login_container">
                    <input typr="email" placeholder="email" name="email" value={userField.email} onChange={handleForm} required />

                    <input typr="password" placeholder="Password" name="password" value={userField.password} onChange={handleForm} required />

                    <input typr="password" placeholder="Confirm Password" name="confirm_password" value={userField.confirm_password} onChange={handleForm} required />

                    <button>Login</button>
                </div>
            </form>
        </div>
    )
}

export default Login
