import React, { useEffect, useState } from 'react'
import axios from 'axios'
const Home = (props) => {
    const id = props.match.params.id
    console.log(id);
    const [items, setItems] = useState([])
    useEffect(() => {
        axios.get('http://localhost:3001/items/')
            .then(response => setItems(response.data))
            .catch(err => console.log(err))
    }, [])

    const handlePurchase = () => {
        axios.get(`http://localhost:3001/items/${id}`)
    }

    return (
        <div>
            <h2 className="welcome">Thank You For Shopping Us!</h2>
            <div className="product-container">
                {items.map(item => ((
                    <div key={item.name} className="product">
                        <h2 className="product-title">{item.name}</h2>
                        <img className="product-logo" src={item.product_img} alt={item.product_name} />
                        <p className="product-description"> {item.description}</p>
                        <h1 className="product-price">${item.price}</h1>
                        <button onClick={handlePurchase} className="product-btn">Buy</button>
                    </div>

                )))}
            </div>
        </div>
    )
}

export default Home
