import React, { useState } from 'react'

const Login = () => {
    const [userField, setUserField] = useState({
        email: '',
        password: '',
        confirm_password: '',
    })
    function handleForm(e) {
        const { name, value } = e.target
        setUserField(prevState => {
            return {
                ...prevState,
                [name]: value
            }
        })
    }
    console.log(userField);
    return (
        <div>
            <form className="login_form">

                <input typr="email" id="email" placeholder="email" name="email" value={userField.email} onChange={handleForm} />

                <input typr="password" id="password" placeholder="Password" name="password" value={userField.password} onChange={handleForm} />

                <input typr="password" id="confirm_password" placeholder="Confirm Password" name="confirm_password" value={userField.confirm_password} onChange={handleForm} />
                <button>Login</button>
            </form>
        </div>
    )
}

export default Login
