import React, { useState } from 'react'
import axios from 'axios'
const AddItem = () => {
    const url = 'http://localhost:3001/additem'

    const [formData, setFormData] = useState({
        Product_name: '',
        Description: '',
        Product_price: '',
        Product_quantity: '',
        Product_img: '',
    })
    function handleChange(event) {
        const { value, name } = event.target
        setFormData(prevdata => ({
            ...prevdata,
            [name]: value
        }))

    }
    console.log(formData);
    function submitForm(event) {
        axios.post(url, {
            Product_name: formData.Product_name,
            Description: formData.Description,
            Product_price: formData.Product_price,
            Product_quantity: formData.Product_quantity,
            product_img: formData.Product_img
        }).then(res => console.log(res))
    }

    return (
        <div className="AddItem">

            <form className="form" onSubmit={(event) => event.preventDefault()}>
                <h1>Add Item</h1>
                <label htmlFor="Product_name">Product_name</label>
                <input type="text" id="Product_name"
                    name="Product_name"
                    value={formData.Product_name}
                    onChange={handleChange}
                    placeholder='Product_name' />

                <label htmlFor="Description">Description</label>
                <input
                    type="text" id="Description"
                    name="Description"
                    value={formData.Description}
                    onChange={handleChange}
                    placeholder='Description' />

                <label htmlFor="Product_price">Product_price</label>
                <input
                    type="text" id="Product_price"
                    name="Product_price"
                    value={formData.Product_price}
                    onChange={handleChange}
                    placeholder='Product_price' />

                <label htmlFor="Product_quantity">Product_quantity</label>
                <input
                    type="text" id="Product_quantity"
                    name="Product_quantity"
                    value={formData.Product_quantity} onChange={handleChange}
                    placeholder='Product_quantity' />

                <label htmlFor="Product_img">Product_img</label>
                <input
                    type="text" id="product_img"
                    name="product_img"
                    value={formData.Product_img}
                    onChange={handleChange}
                    placeholder='Product_img' />
                <button className="form-control">Add</button>
            </form>
        </div>
    )
}

export default AddItem
