import React, { useState } from 'react'
import { signInWithGooglePopup } from '../Firebase'
const Login = () => {
    const [userField, setUserField] = useState({
        email: '',
        password: '',
        confirm_password: '',
    })
    function handleForm(e) {
        const { name, value } = e.target
        setUserField(prevState => {
            return {
                ...prevState,
                [name]: value
            }
        })
    }
    const signInWithGoogle = async () => {
        const { user } = await signInWithGooglePopup();
        await createUserDocumentFromAuth(user);
    };
    const handleSubmit = async (event) => {
        event.preventDefault();

        try {
            const response = await signInAuthUserWithEmailAndPassword(
                email,
                password
            );
            console.log(response);
            resetFormFields();
        } catch (error) {
            switch (error.code) {
                case 'auth/wrong-password':
                    alert('incorrect password for email');
                    break;
                case 'auth/user-not-found':
                    alert('no user associated with this email');
                    break;
                default:
                    console.log(error);
            }
        }
    };
    return (
        <div>
            <form className="login_form" onSubmit={handleSubmit}>
                <div className="login_container">
                    <input typr="email" placeholder="email" name="email" value={userField.email} onChange={handleForm} required />

                    <input typr="password" placeholder="Password" name="password" value={userField.password} onChange={handleForm} required />

                    <input typr="password" placeholder="Confirm Password" name="confirm_password" value={userField.confirm_password} onChange={handleForm} required />

                    <button>Login</button>
                    <button onClick={signInWithGoogle}>Google Sign In</button>
                </div>
            </form>
        </div>
    )
}

export default Login
