import React, { useState } from 'react'
import axios from 'axios'
const AddItem = () => {
    const url = 'http://localhost:3001/items/add'

    const [formData, setFormData] = useState({
        name: '',
        description: '',
        price: '',
        quantity: '',
        product_img: '',
    })
    function handleChange(event) {
        const { value, name } = event.target
        setFormData(prevdata => ({
            ...prevdata,
            [name]: value
        }))

    }
    console.log(formData);
    function submitForm() {
        axios.post(url, {
            name: formData.name,
            description: formData.description,
            price: formData.price,
            quantity: formData.quantity,
            product_img: formData.product_img
        }).then(res => console.log(res))
    }

    return (
        <div className="AddItem">

            <h1>Add Item</h1>
            <form className="form" onSubmit={(event) => event.preventDefault()} action="/data">
                <label htmlFor="name">name</label>
                <input type="text" id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    placeholder='Product_name' />

                <label htmlFor="description">description</label>
                <input
                    type="text" id="description"
                    name="description"
                    value={formData.description}
                    onChange={handleChange}
                    placeholder='Description' />

                <label htmlFor="price">price</label>
                <input
                    type="text" id="price"
                    name="price"
                    value={formData.price}
                    onChange={handleChange}
                    placeholder='price' />

                <label htmlFor="quantity">quantity</label>
                <input
                    type="text" id="quantity"
                    name="quantity"
                    value={formData.quantity} onChange={handleChange}
                    placeholder='quantity' />
                <label htmlFor="product_img">product_img</label>
                <input
                    type="text" id="product_img"
                    name="product_img"
                    value={formData.product_img} onChange={handleChange}
                    placeholder='product_img' />

                <button onClick={submitForm} className="form-control">Add</button>
            </form>
        </div>
    )
}

export default AddItem
